      </header>

      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <div className="traits-container">
          {traits.map((trait, index) => (
            <div key={index} className="trait-card">
              {trait}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
